 // BEGINN OF FEDITOR-JAVASCRIPT 'mapping'
jQuery(function(){

	var getTabElement = function(){
	
		//jQuery('.tabs').tabs();
	
	};
	
	
	var maschineSlider = function(){
		
		jQuery('.slider').slick({
			slidesToShow: 4,
			slidesToScroll: 1,
			arrows: true,
			dots: false,
			adaptiveHeight: true,
			autoplay:false,
			autoplaySpeed:6000,
			speed:1000,
			responsive:[{
				breakpoint: 992,
				settings: {
					slidesToShow: 3,
					slidesToScroll: 1
			}},{
				breakpoint: 768,
				settings: {
					slidesToShow: 2,
					slidesToScroll: 1
			}},{
				breakpoint: 480,
				settings: {
					slidesToShow: 1,
					slidesToScroll: 1
			}}]
		});
		
	};
	
	var maschineFunktionsbeschreibung = function(){
		jQuery('.funktionsbeschreibung-img-mobile').slick({
			slidesToShow: 1,
			slidesToScroll: 1,
			arrows: true,
			dots: false,
			adaptiveHeight: true,
			autoplay:false,
			autoplaySpeed:6000,
			speed:1000,
			prevArrow:'<div class="slick-prev"></div>',
			nextArrow:'<div class="slick-next"></div>'
		});
		
	};
	
	var maschineTechnischeAccordion = function(){
		
		jQuery('.maschine-technische.mobile').collabsible({
			animation: true,
			closeAble: true,
			closeOther: true,
			slideSpeed: 125
		});
		
	};
	
	var maschineZertifizierungenAccordion = function(){
		
		jQuery('.maschine-zertifizierungen.mobile .maschine-zertifizierungen-inner .cr').collabsible({
			animation: true,
			closeAble: true,
			closeOther: true,
			slideSpeed: 125
		});
		
	};
	
	var maschinedownloadsAccordion = function(){
		
		jQuery('.maschine-downloads.mobile .maschine-downloads-inner .cr').collabsible({
			animation: true,
			closeAble: true,
			closeOther: true,
			slideSpeed: 125
		});
		
	};
	
	var notification = function(){
		
		var obj = jQuery('.notification');
		var close = obj.find('.close');
		
		close.on('click', function(){
			jQuery(this)
				.parent(obj)
				.removeClass('show')
				.addClass('hide')
				.delay(1000)
				.queue(function(){
					jQuery(this).removeClass('hide');
					jQuery(this).dequeue();
				});
		});
		
	};
	
	jQuery('.tx-j77products .maschine-teaser').on('click', function() {
		jQuery('.tx-j77products .maschine-teaser + .overlay').removeClass('active');
		jQuery('.tx-j77products .maschine-teaser').removeClass('active');
		
		var wrapper = jQuery(this).closest('.tx-j77products-list');
		jQuery(this).siblings('.overlay').addClass('active').css('left', (jQuery(window).width() / 2) - wrapper.offset().left);
		jQuery(this).addClass('active');
	});
	
	jQuery(window).on('resize', function() {
		var ol = jQuery('.overlay.active');
		if(ol.length) {
			var wrapper = jQuery(ol).closest('.tx-j77products-list');
			ol.css('left', (jQuery(window).width() / 2) - wrapper.offset().left);
		}
	});
	

	jQuery('.tx-j77products .overlay').on('click', function() {
		jQuery('.tx-j77products .maschine-teaser + .overlay').removeClass('active');
		jQuery('.tx-j77products .maschine-teaser').removeClass('active');
	});
	
	/*
	jQuery('.tx-j77products .maschine-teaser .overlay-open').on('click', function() {
		jQuery('.tx-j77products .maschine-teaser + .overlay').removeClass('active');
		jQuery('.tx-j77products .maschine-teaser').removeClass('active');
		jQuery(this).parent().siblings('.overlay').addClass('active');
		jQuery(this).parent().addClass('active');
	});

	jQuery('.tx-j77products .overlay .overlay-close').on('click', function() {
		jQuery('.tx-j77products .maschine-teaser + .overlay').removeClass('active');
		jQuery('.tx-j77products .maschine-teaser').removeClass('active');
	});
	*/
	
	// call functions
	
	maschineSlider();
	maschineFunktionsbeschreibung();
	maschineTechnischeAccordion();
	maschineZertifizierungenAccordion();
	maschinedownloadsAccordion();
	notification();
	getTabElement();

}); // document ready

// END OF FEDITOR-JAVASCRIPT 'mapping'